<?php 
if(isset($_GET['BatchGenQC'])){
    include 'config/db.php';
//ประกาศตัวแปรรับค่าจาก param method get
$BatchGenQC = $_GET['BatchGenQC'];
$stmt = $conn->prepare('DELETE FROM tracking WHERE BatchGenQC=:BatchGenQC');
$stmt->bindParam(':BatchGenQC', $BatchGenQC , PDO::PARAM_INT);
$stmt->execute();

  if($stmt->rowCount() > 0){
        echo '<script>       
              window.location = "data_host.php"; //หน้าที่ต้องการให้กระโดดไป
              </script>';
    }else{
       echo '<script>         
              window.location = "data_host.php"; //หน้าที่ต้องการให้กระโดดไป
             </script>';
    }
$conn = null;
} //isset
